function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=200?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const modalBtn1=document.querySelector(".btn1"),modalBg1=document.querySelector(".modal-bg"),modalClose=document.querySelector(".modal-close");modalBtn1.addEventListener("click",(function(){modalBg1.classList.add("bg-active")})),modalClose.addEventListener("click",(function(){modalBg1.classList.remove("bg-active")}));const modalBtn2=document.querySelector(".btn2"),modalBg2=document.querySelector(".modal-bg2"),modalClose2=document.querySelector(".modal-close2");modalBtn2.addEventListener("click",(function(){modalBg2.classList.add("bg-active")})),modalClose2.addEventListener("click",(function(){modalBg2.classList.remove("bg-active")}));